package demojava;

public class Ifelseifladder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String city = "achanta";  
		if(city == "Meerut") {  
		System.out.println("city is tpalem");  
		}else if (city == "Noida") {  
		System.out.println("city is palkol");  
		}else if(city == "Agra") {  
		System.out.println("city is bimavaram");  
		}else {  
		System.out.println(city);  
		}  
	}

}
